<?phpinclude_once('startup.php');include_once('model.php');// Установка параметров, подключение к БД, запуск сессии.startup();// Извлечение статьи.if (!isset($_GET['id']))    header('Location: editor.php');$id = intval($_GET['id']);$article = $id ? articles_get($id) : false;if (!$article)    header('Location: editor.php');$title = $article['title'];$content = $article['content'];// Обработка отправки формы.if (!empty($_POST)) {    if (!empty($_POST['title']) && !empty($_POST['content']) && articles_edit($id, $_POST['title'], $_POST['content'])) {        header('Location: editor.php');        $NotDate = true;        die();    } else {/*  $title = $_POST['title'];    $content = $_POST['content'];    $error = true;*/    $NotDate = NULL;    }}// Кодировка.header('Content-type: text/html; charset=utf-8');// Вывод в шаблон.include ('theme/edit.php');